'use strict';function STS(selectData){window.addEventListener('DOMContentLoaded',main);function main(){var primaryDom=$('#sts_wrap [data-primary="true"]');var subDom=$('#sts_wrap select:not([data-primary="true"])');var values=getValuesByKey(selectData,primaryDom.getAttribute('data-key')); // render primary and sub select 
primaryDom.innerHTML=getOptionsByArray(values);subDom.innerHTML=getOptionsByArray(selectData[0][subDom.getAttribute('data-key')]); // handle change primary select
primaryDom.addEventListener('change',function(e){return onChangePrimary(e,primaryDom,subDom);});}function onChangePrimary(e,primaryDom,subDom){var key=primaryDom.getAttribute('data-key');var subKey=subDom.getAttribute('data-key');var value=e.target.value;var subValues=getArrayByEqual(selectData,key,value,subKey);subDom.innerHTML=getOptionsByArray(subValues);}function $(selector){return document.querySelector(selector);}function getArrayByEqual(array,key,value,subKey){for(var i in array){if(array[i][key]===value){return array[i][subKey];break;}}}function getValuesByKey(array,key,i){var returnArr=[];for(var _i in array){returnArr.push(array[_i][key]);}return returnArr;} /**
   * params: [value...]
   * return: string 
   */function getOptionsByArray(array){var options='';for(var i in array){options+=renderOptionTpl(array[i]);}return options;}function renderOptionTpl(value){return '<option value=\''+value+'\'>'+value+'</option>';}}
